'use strict';
const Util = require('./lib/util');


return Util.shouldPublish().then((shouldPublish) => {
  if (shouldPublish) {
      return Util.publish();
  }
}).catch((err) => {
  if (err instanceof Util.CliError) {
      console.error(err.toString());
  } else {
      console.error(err);
  }
  process.exit(1);
});
