'use strict';
const Execa = require('execa');
const Semver = require('semver');
const _ = require('lodash');


exports.getCurrentSemverTags = () => {
    return Execa.stdout('git', [ 'tag', '-l', '--points-at', 'HEAD']).then((tags) => {
        return _(tags.split('\n'))
            .map(Semver.valid.bind(Semver))
            .filter()
            .value();
    });
};

